import React, { useState, useEffect } from "react";
import { Container } from 'react-grid-system';
import { api } from '../Data/data'
import Button from '@mui/material/Button';
import TextField from "@mui/material/TextField";
import Box from '@mui/material/Box';
import Autocomplete from '@mui/material/Autocomplete';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
import ThermostatIcon from '@mui/icons-material/Thermostat';
import InvertColorsIcon from '@mui/icons-material/InvertColors';
import AirIcon from '@mui/icons-material/Air';
import { useLocation, Link } from 'react-router-dom';
import Grid from '@mui/material/Grid';
import { Row, Col } from 'react-grid-system'
import ReplayIcon from '@mui/icons-material/Replay';
import CircularProgress from '@mui/material/CircularProgress';

function PruebaComponent() {
  //Var

  const [clima, setClima] = useState({});
  const [ciudades, setCiudades] = useState([]);
  const [inputValue, setInputValue] = React.useState('');
  const [hora, setHora] = useState(null);

  const { search } = useLocation();
  const queryParams = new URLSearchParams(search)
  const ciudad = queryParams.get('ciudad');
  const [busquedaCiudad, setBusquedaCiudad] = useState(false);

  //Effect
  useEffect(() => {
    async function isFocused() {
      if (ciudad !== null) {
        getClimaCiudad(ciudad)
      } else {
        getCiudades();
      }

    }
    isFocused()
  }, [])

  const getCiudades = async () => {
    await api.ciudades()
      .then(response => {
        let cd = []
        for (let i = 0; i < 100000; i++) {
          if (i % 1000 == 0) {
            cd.push(response.data[i])
          }
        }
        console.log(cd)
        setCiudades(cd)
      })
  }

  const getClima = async (posicion) => {
    await api.tiempo(posicion.latitude, posicion.longitude)
      .then(response => {
        console.log(response.data)
        setClima(response.data)
      })
      .catch(err => {
        console.error(err)
      })
  }

  const getClimaCiudad = async (ciudad) => {
    await api.climaCiudad(ciudad)
      .then(response => {
        console.log(response.data)
        let coords = response.data.coord
        console.log(coords)
        getNextDaysCity(coords)
        setClima(response.data)
        setBusquedaCiudad(true);
      })
      .catch(err => {
        console.error(err)
      })
  }

  const getNextDays = async (posicion) => {
    await api.nextDays(posicion.latitude, posicion.longitude)
      .then(response => {
        console.log(response.data)
        const timeZone = response.data.timezone
        console.log(timeZone)
        setHora(new Date().toLocaleString("es-ES", { hour: '2-digit', minute: '2-digit', timeZone: timeZone }))
      })
      .catch(err => {
        console.error(err)
      })
  }

  const getNextDaysCity = async (posicion) => {
    await api.nextDays(posicion.lat, posicion.lon)
      .then(response => {
        console.log(response.data)
        const timeZone = response.data.timezone
        console.log(timeZone)
        setHora(new Date().toLocaleString("es-ES", { hour: '2-digit', minute: '2-digit', timeZone: timeZone }))
      })
      .catch(err => {
        console.error(err)
      })
  }

  //Function
  function click() {
    async function isFocused() {
      await navigator.geolocation.getCurrentPosition(function (position) {
        setBusquedaCiudad(true);
        getNextDays(position.coords)
        getClima(position.coords)
      })
    }
    isFocused()
  }

  function buscarCiudad(ciudad) {
    getClimaCiudad(ciudad.name + ',' + ciudad.country);
    console.log(ciudad.name + ',' + ciudad.country)
  }

  function menu() {
    window.location.href = "/clima";
  }


  return (
    <div className="App">
      <header className="App-header">
        <Container>
          {hora && clima.name && busquedaCiudad == true ?
            <Grid container columnSpacing={5}>
              <Grid item>
                <Card>
                  {
                    <>
                      <CardContent>
                        <Typography sx={{ fontSize: 25 }} color="text.primary">
                          {clima.name}
                          <br />
                          {hora}
                        </Typography>
                        <img style={{ width: 150 }} key={`img_1`} alt={`clima_1`} src={'img/' + clima.weather[0].icon + '.svg'} />
                        <Typography sx={{ fontSize: 30 }} color="text.primary">
                          {Math.round(clima.main.temp)}째C
                          <br />
                          {clima.weather[0].description.charAt(0).toUpperCase() + clima.weather[0].description.slice(1)}
                        </Typography>
                        <br />
                        <Typography sx={{ fontSize: 25 }} color="text.secondary">
                          <ThermostatIcon />
                          <b>Max:</b> {Math.round(clima.main.temp_max)}째C <b>Min:</b> {Math.round(clima.main.temp_min)}째C
                        </Typography>
                        <Typography sx={{ fontSize: 25 }} color="text.secondary">
                          <InvertColorsIcon />
                          Humedad: {clima.main.humidity}%
                        </Typography>
                        <Typography sx={{ fontSize: 25 }} color="text.secondary">
                          <AirIcon />
                          Viento: {Math.round(clima.wind.speed)} Km/h
                        </Typography>
                        <br />
                        <Row>
                          <Col>
                            <Button variant="contained" onClick={menu}>
                              Volver
                            </Button>
                          </Col>
                          <Col>
                            <a href={`/clima?ciudad=${clima.name},${clima.sys.country}`}>
                              <ReplayIcon />
                            </a>
                          </Col>
                        </Row>
                      </CardContent>
                    </>
                  }
                </Card>
              </Grid>
              {/* <Grid item>
              <Card>
                
              </Card>
            </Grid> */}
            </Grid>
            : (ciudad == null && busquedaCiudad == false) ?
              <>
                <Autocomplete
                  id="country-select-demo"
                  onChange={(event, newValue) => {
                    setBusquedaCiudad(true);
                    buscarCiudad(newValue);
                  }}
                  inputValue={inputValue}
                  onInputChange={(event, newInputValue) => {
                    setInputValue(newInputValue);
                  }}
                  sx={{ width: 300 }}
                  options={ciudades}
                  autoHighlight
                  getOptionLabel={(option) => option.name}
                  renderOption={(props, option) => (
                    <Box component="li" sx={{ '& > img': { mr: 2, flexShrink: 0 } }} {...props}>
                      <img
                        loading="lazy"
                        width="20"
                        src={`https://flagcdn.com/w20/${option.country.toLowerCase()}.png`}
                        srcSet={`https://flagcdn.com/w40/${option.country.toLowerCase()}.png 2x`}
                        alt=""
                      />
                      {option.name} ({option.country})
                    </Box>
                  )}
                  renderInput={(params) => (
                    <TextField
                      {...params}
                      label="Busca una ciudad"
                      inputProps={{
                        ...params.inputProps,
                        autoComplete: 'new-password', // disable autocomplete and autofill
                      }}
                    />
                  )}
                />
                <br /><br />
                <Button variant="contained" onClick={click}>
                  Usar mi ubicaci처n
                </Button>
              </>
              :
              <Box sx={{ display: 'flex' }}>
                <CircularProgress />
              </Box>
          }
        </Container>
      </header>
    </div>
  );
}

export default PruebaComponent;
