import { width } from '@mui/system';
import React, { useEffect, useState } from 'react';
import { Col, Container, Spinner } from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import { api } from '../API/api';

function Evento() {
    let data = useParams();
    let id = data.id;

    const [evento, setEvento] = useState(false);
    const [asientos, setAsientos] = useState(false);

    const getEvento = async () => {
        await api.getEvento(id)
            .then(response => {
                setEvento(response.data);
                getAsientos();
                optionsFunc();
            }).catch(error => {
                window.location.href = "/";
                console.log(error)
            })
    }

    const optionsFunc = async() => {
        let options = '';
        for (let i = 1; i <= asientos; i++){
            options += "<option value='" + i + "'>" + i + "</option>";
            if(i == 5){
                break;
            }
        }
        document.getElementById('numeroEntradas').innerHTML = options;
    }

    const getAsientos = async () => {
        let tipo = document.getElementById('tipoEntrada').value;
        await api.getAsientosDisponibles(tipo, id)
            .then(response => {
                setAsientos(response.data);
                console.log(response.data)
            })
    }

    useEffect(() => {
        async function showEvento() {
            getEvento();
        }
        showEvento()
    }, [])


    if (evento) {
        return (
            <>
                <Container className='tarjetaEvento'>
                    <div className='datosEvento'>
                        <h5>{evento.fecha}</h5>
                        <h2><b>{evento.nombre}</b></h2>
                        <h4><b>{evento.ciudad}</b></h4>
                        <h6>{evento.descripcion}</h6>
                    </div>
                    <img src={'/img/eventos/evento_' + evento.id + '.jpg'} />
                </Container>
                <Container>
                    <div className='formEntradas'>
                        <h6>Tipo de entrada
                            <select id='tipoEntrada' defaultValue={1} onChange={getAsientos}>
                                <option value='1'>General</option>
                                <option value='2'>VIP</option>
                            </select>
                        </h6>
                        <h6>Entradas
                            <select id='numeroEntradas' defaultValue={1}>
                                
                            </select>
                        </h6>
                        { options != ''
                        ?
                        <div className='datosEntrada' id='entrada_1'>
                            <h6>Entrada NÂº1</h6>
                            <form>
                                <input placeholder='Nombre'></input><br />
                                <input placeholder='Apellido'></input><br />
                                <input placeholder='Fecha de nacimiento'></input><br />
                                <input placeholder='DNI'></input>
                            </form>
                        </div>
                        :
                        <div>Entradas agotadas</div>
                        }
                    </div>
                </Container>
            </>
        )
    } else {
        return (
            <Container className='home'>
                <div className='eventos'>
                    <Spinner animation="border" style={{ marginBottom: 40 }} />
                </div>
            </Container>
        )
    }
}
export default Evento;
