import { CompareArrowsSharp } from "@mui/icons-material";
import { useEffect, useState } from "react";
import { Container, Spinner } from "react-bootstrap"
import { useParams } from "react-router-dom";
import { api } from "../API/api";

function Compra() {

    let data = useParams();
    let id = data.id;

    const [compra, setCompra] = useState(false);
    const [ticketCount, setCount] = useState(false);

    const getCompra = async () => {
        await api.getCompra(id)
            .then(response => {
                setCount(Object.keys(response.data.Tickets).length)
                setCompra(response.data);
            }).catch(error => {
                window.location.href = "/";
                console.log(error)
            })
    }

    useEffect(() => {
        async function showCompra() {
            getCompra();
        }
        showCompra()
    }, [])

    if (compra) {
        return (
            <Container>
                <h2 style={{ marginTop: 40 }}>Gracias por tu compra!</h2>
                <p><b>Número de referencia:</b> #{compra.Compra.id}</p>
                <p>
                    <b>Nº de entradas:</b> {ticketCount}<br />
                    <b>Total:</b> {compra.Compra.importe}€
                </p>
                <div className="ticketCard">
                    {compra.Tickets.map((value, index) => {
                        return (
                            <div class="item">
                                <div class="item-right">
                                    <h2 class="num">23</h2>
                                    <p class="day">Feb</p>
                                    <span class="up-border"></span>
                                    <span class="down-border"></span>
                                </div>

                                <div class="item-left">
                                    <p class="event">Music Event</p>
                                    <h2 class="title">Live In Sydney</h2>

                                    <div class="sce">
                                        <div class="icon">
                                            <i class="fa fa-table"></i>
                                        </div>
                                        <p>Monday 15th 2016 <br /> 15:20Pm  11:00Am</p>
                                    </div>
                                    <div class="fix"></div>
                                    <div class="loc">
                                        <div class="icon">
                                            <i class="fa fa-map-marker"></i>
                                        </div>
                                        <p>North,Soth, United State , Amre <br /> Party Number 16,20</p>
                                    </div>
                                    <div class="fix"></div>
                                    <button class="tickets">Ver ticket</button>
                                </div>
                            </div>
                        )
                    })}
                </div>
            </Container>
        )
    } else {
        return (
            <Container className='home'>
                <div className='eventos'>
                    <Spinner animation="border" style={{ marginBottom: 40 }} />
                </div>
            </Container>
        )
    }

}
export default Compra
