import React, { useEffect, useState } from 'react'
import { Card, Container, Row, Col, Spinner } from 'react-bootstrap'
import { api } from '../API/api';

function Home() {

    const [eventos, setEventos] = useState(false);

    const getEventos = async () => {
        await api.homeEventos()
            .then(response => {
                setEventos(response.data);
            })
    }

    useEffect(() => {
        async function showEventos() {
            getEventos();
        }
        showEventos()
    }, [])

    if (eventos) {
        return (
            <Container className='home'>
                <div style={{ marginBottom: 40 }}>
                    <form>
                        <input type='text'></input>
                        <button type='submit'>Buscar</button>
                    </form>
                </div>
                <div className='eventos'>
                    <Row xl={3} l={3} md={2} s={2} xs={1}>
                        {eventos.map((value, index) => {
                            console.log(value)
                            return (
                                <a href={value.id}>
                                    <Col style={{ marginBottom: 40 }}>
                                        <Card key={index}>
                                            <Card.Img variant="top" src="holder.js/100px160" />
                                            <Card.Body>
                                                <Card.Title><h4>{value.nombre}</h4></Card.Title>
                                                <Card.Text>
                                                    <span className='tituloCiudad'>{value.ciudad}</span><br />
                                                    {value.descripcion}
                                                </Card.Text>
                                            </Card.Body>
                                        </Card>
                                    </Col>
                                </a>
                            )
                        })}
                    </Row>
                </div>
            </Container>
        )
    } else {
        return (
            <Container className='home'>
                <div style={{ marginBottom: 40 }}>
                    <form>
                        <input type='text'></input>
                        <button type='submit'>Buscar</button>
                    </form>
                </div>
                <div className='eventos'>
                <Spinner animation="border" />
                </div>
            </Container>
        )
    }


}
export default Home;
