import { useEffect, useState } from "react";
import { Container, Spinner } from "react-bootstrap"
import { useParams } from "react-router-dom";
import { api } from "../API/api";

function Compra(){

    let data = useParams();
    let id = data.id;

    const [compra, setCompra] = useState(false);

    const getCompra = async () => {
        await api.getCompra(id)
            .then(response => {
                setCompra(response.data);
                const ticketCount = Object.keys(compra.Tickets).length
            }).catch(error => {
                window.location.href = "/";
                console.log(error)
            })
    }

    useEffect(() => {
        async function showCompra() {
            getCompra();
        }
        showCompra()
    }, [])

if(compra){
    return(
        <Container>
            <h2>Gracias por tu compra!</h2>
            <p>Compra #{compra.Compra.id}</p>
            <p>NÂº de entradas: {ticketCount}</p>
        </Container>
    )
} else {
    return (
        <Container className='home'>
            <div className='eventos'>
                <Spinner animation="border" style={{ marginBottom: 40 }} />
            </div>
        </Container>
    )
}

}
export default Compra
