import { width } from '@mui/system';
import React, { useEffect, useState } from 'react';
import { Col, Container, Spinner } from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import { api } from '../API/api';

function Evento() {
    let data = useParams();
    let id = data.id;

    const [evento, setEvento] = useState(false);

    const getEvento = async () => {
        await api.getEvento(id)
            .then(response => {
                setEvento(response.data);
            })
    }

    useEffect(() => {
        async function showEvento() {
            getEvento();
        }
        showEvento()
    }, [])


    if (evento) {
        return (
            <>
                <Container className='tarjetaEvento'>
                    <div className='datosEvento'>
                        <h5>{evento.fecha}</h5>
                        <h2><b>{evento.nombre}</b></h2>
                        <h4><b>{evento.ciudad}</b></h4>
                        <h6>{evento.descripcion}</h6>
                    </div>
                    <img src={'/img/eventos/evento_' + evento.id + '.jpg'} />
                </Container>
                <Container>
                    <div className='formEntradas'>
                        <h6>Tipo de entrada
                            <select defaultValue={1}>
                                <option value='1'>General</option>
                                <option value='2'>VIP</option>
                            </select>
                        </h6>
                        <h6>Entradas
                            <select defaultValue={1}>
                                <option value='1'>1</option>
                                <option value='2'>2</option>
                                <option value='3'>3</option>
                                <option value='4'>4</option>
                                <option value='5'>5</option>
                            </select>
                        </h6>
                        <div className='datosEntrada' id='entrada_1'>
                            <h6>Entrada Nº1</h6>
                            <form>
                                <input placeholder='Nombre'></input><br />
                                <input placeholder='Apellido'></input><br />
                                <input placeholder='Fecha de nacimiento'></input><br />
                                <input placeholder='DNI'></input>
                            </form>
                        </div>
                    </div>
                </Container>
            </>
        )
    } else {
        return (
            <Container className='home' style={{ marginBottom: 40 }}>
                <div className='eventos'>
                    <Spinner animation="border" />
                </div>
            </Container>
        )
    }
}
export default Evento;
