import axios from "axios";

export const url_api = process.env.REACT_APP_OPENWEATHER_URL
export const api_key = process.env.REACT_APP_API_OPENWEATHER

export const api = {
    tiempo: function(lat, lon){
        return axios.get(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${api_key}&lang=es&units=metric`);
    },
    ciudades: function(){
        return axios.get("https://raw.githubusercontent.com/lutangar/cities.json/master/cities.json");
    },
    climaCiudad: function(ciudad){
        return axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${ciudad}&appid=${api_key}&lang=es&units=metric`);
    },
    nextDays: function(lat, lon){
        return axios.get(`https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&appid=${api_key}&lang=es&units=metric`)
    }
}
