import { ConstructionOutlined } from '@mui/icons-material';
import React, { useEffect, useState } from 'react'
import { Navbar, Nav, Container, Modal, Button } from 'react-bootstrap'
import { api } from '../API/api';

function NavBar() {
    const [showLogin, setShowLogin] = useState(false);
    const [showRegister, setShowRegister] = useState(false);

    const handleCloseLogin = () => setShowLogin(false);
    const handleShowLogin = () => setShowLogin(true);
    const handleCloseRegister = () => setShowRegister(false);
    const handleShowRegister = () => setShowRegister(true);

    const [logged, setLogged] = useState(false);

    const submitRegister = async () => {
        var nombre = document.getElementById('nombre').value;
        var apellido = document.getElementById('apellido').value;
        var email = document.getElementById('email').value;
        var password = document.getElementById('password').value;

        await api.register(nombre, apellido, email, password)
            .then(response => {
                console.log(response.data);
            })
    }

    useEffect(() => {
      async function loggedIn(){
          getLogged();
      }
      loggedIn()
    }, [])

    const getLogged = async () => {
        const getCookieValue = (name) => (
            document.cookie.match('(^|;)\\s*' + name + '\\s*=\\s*([^;]+)')?.pop() || ''
        )
        const token = getCookieValue('sess')
        console.log(token)

        if(token !== ''){
            await api.isLogged(token).then(response => {
                setLogged(response.data.id)
            }).catch(error => {
                setLogged(false)
                console.log(error)
            })
        }

    }

    const submitLogin = async () => {
        var email = document.getElementById('email').value;
        var password = document.getElementById('password').value;

        await api.login(email, password)
            .then(response => {
                document.cookie = 'sess=' + response.data + '; max-age=3600';
                window.location.reload(false);
            })
    }

    return (
        <Navbar collapseOnSelect expand="lg" bg="light" variant="light">
            <Container>
                <Navbar.Brand href="/"><img alt='Compralas' src='img/compralas logo.png' width='90px' /></Navbar.Brand>
                <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                <Navbar.Collapse id="responsive-navbar-nav">
                    <Nav className="me-auto">
                        <Nav.Link href="/">Home</Nav.Link>
                        <Nav.Link href="/eventos">Eventos</Nav.Link>
                        <Nav.Link href="/sobre-nosotros">Sobre nosotros</Nav.Link>
                    </Nav>
                    <Nav>
                        {logged == false
                        ? 
                        <>
                        <Nav.Link onClick={handleShowLogin}>Log in</Nav.Link> 
                        <Nav.Link onClick={handleShowRegister} id="btn">Regístrate</Nav.Link>
                        </>
                        : 
                        <Nav.Link>Mi cuenta</Nav.Link>
                        }
                    </Nav>
                </Navbar.Collapse>
            </Container>

            <Modal show={showLogin} onHide={handleCloseLogin}>
                <Modal.Header closeButton>
                    <Modal.Title>Iniciar sesión</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <div className="Login">
                        <form>
                            <label htmlFor="email">Email</label><br />
                            <input type="text" id="email" /><br />
                            <label htmlFor="password">Contraseña</label><br />
                            <input type="text" id="password" /><br /><br />
                        </form>
                        <Button onClick={submitLogin}>Acceder</Button>
                    </div>
                </Modal.Body>
            </Modal>

            <Modal show={showRegister} onHide={handleCloseRegister}>
                <Modal.Header closeButton>
                    <Modal.Title>Regístrate</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <div className="Signup">
                        <form>
                            <label htmlFor="nombre">Nombre</label><br />
                            <input type="text" id="nombre" /><br />
                            <label htmlFor="apellido">Apellido</label><br />
                            <input type="text" id="apellido" /><br />
                            <label htmlFor="email">Email</label><br />
                            <input type="text" id="email" /><br />
                            <label htmlFor="password">Contraseña</label><br />
                            <input type="text" id="password" /><br /><br />
                        </form>
                        <Button onClick={submitRegister}>Registrar</Button>
                    </div>
                </Modal.Body>
            </Modal>
        </Navbar>
    )

}

export default NavBar;
