import { CompareArrowsSharp } from "@mui/icons-material";
import { useEffect, useState } from "react";
import { Container, Spinner } from "react-bootstrap"
import { useParams } from "react-router-dom";
import { api } from "../API/api";

function Compra(){

    let data = useParams();
    let id = data.id;

    const [compra, setCompra] = useState(false);
    const [ticketCount, setCount] = useState(false);

    const getCompra = async () => {
        await api.getCompra(id)
            .then(response => {
                setCount(Object.keys(response.data.Tickets).length)
                setCompra(response.data);
            }).catch(error => {
                window.location.href = "/";
                console.log(error)
            })
    }

    useEffect(() => {
        async function showCompra() {
            getCompra();
        }
        showCompra()
    }, [])

if(compra){
    return(
        <Container>
            <h2>Gracias por tu compra!</h2>
            <p>Compra #{compra.Compra.id}</p>
            <p>NÂº de entradas: {ticketCount}</p>
            <div>
            {compra.Tickets.map((value, index) => {
                            return (
                                <a key={index} href={'/evento/' + value.id}>
                                    <Col style={{ marginBottom: 40 }}>
                                        <Card key={index}>
                                            <Card.Img variant="top" src={url_api + '/img/evento_' + value.id + '.jpg'} />
                                            <Card.Body>
                                                <Card.Title><h4>{value.nombre}</h4></Card.Title>
                                                <Card.Text>
                                                    <span className='tituloCiudad'>{value.ciudad}</span><br />
                                                    {value.descripcion}
                                                </Card.Text>
                                            </Card.Body>
                                        </Card>
                                    </Col>
                                </a>
                            )
                        })}
            </div>
        </Container>
    )
} else {
    return (
        <Container className='home'>
            <div className='eventos'>
                <Spinner animation="border" style={{ marginBottom: 40 }} />
            </div>
        </Container>
    )
}

}
export default Compra
