import { width } from '@mui/system';
import React, { useEffect, useState } from 'react';
import { Col, Container, Spinner } from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import { api } from '../API/api';

function Evento() {
    let data = useParams();
    let id = data.id;

    const [evento, setEvento] = useState(false);

    const getEvento = async () => {
        await api.getEvento(id)
            .then(response => {
                setEvento(response.data);
            })
    }

    useEffect(() => {
        async function showEvento() {
            getEvento();
        }
        showEvento()
    }, [])


    if (evento) {
        return (
            <Container className='tarjetaEvento'>
                <div className='datosEvento'>
                    <h5>{evento.fecha}</h5>
                    <h2><b>{evento.nombre}</b></h2>
                    <h4><b>{evento.ciudad}</b></h4>
                    <h6>{evento.descripcion}</h6>
                </div>
                <div className='fotoEvento'>
                    <img src={'/img/eventos/evento_' + evento.id}/>
                </div>
            </Container>
        )
    } else {
        return (
            <Container className='home'>
                <div style={{ marginBottom: 40 }}>
                    <form>
                        <input type='text'></input>
                        <button type='submit'>Buscar</button>
                    </form>
                </div>
                <div className='eventos'>
                    <Spinner animation="border" />
                </div>
            </Container>
        )
    }
}
export default Evento;
