import { useEffect, useState } from "react";
import { Container, Spinner } from "react-bootstrap"
import { api } from "../API/api";

function MisCompras() {

    const getCookieValue = (name) => (
        document.cookie.match('(^|;)\\s*' + name + '\\s*=\\s*([^;]+)')?.pop() || ''
    )

    let token = getCookieValue('sess');

    const [compras, setCompras] = useState(false);
    const [evento, setEvento] = useState(false);

    const getCompras = async () => {
        await api.getComprasUsuario(token)
            .then(response => {
                setCompras(response.data);
                console.log(response.data);
            }).catch(error => {
                window.location.href = "/";
                console.log(error)
            })
    }

    useEffect(() => {
        async function showCompras() {
            getCompras();
        }
        showCompras()
    }, [])


    if (compras) {
        return (
            <Container>
                <h2 style={{ marginTop: 40 }}>Tus compras</h2>
                <table>
                    <thead>
                    <tr>
                        <th>ID</th>
                        <th>Importe</th>
                        <th>Número de entradas</th>
                        <th></th>
                    </tr>
                    </thead>
                    <tbody>
                    {compras.map((value, index) => {
                        return (
                            <tr key={index}>
                                <td>#{value.id}</td>
                                <td>{value.importe}€</td>
                            </tr>
                        )
                    })}
                    </tbody>
                </table>
            </Container>
        )
    } else {
        return (
            <Container className='home'>
                <div className='eventos'>
                    <Spinner animation="border" style={{ marginBottom: 40 }} />
                </div>
            </Container>
        )
    }

}

export default MisCompras
